<template>
  <div class="loading">
    <div class="box">
      <p>
        <img
          src="data:image/png;base64,"
          alt=""
        />
      </p>
      <p style="margin-top: 40px; height: 70px">
        <el-input
          v-model="form.mobile"
          placeholder="13800000002"
          style="height: 100%"
          prefix-icon="el-icon-user"
        ></el-input>
      </p>
      <p style="margin-top: 20px">
        <el-input
          v-model="form.password"
          placeholder="123456"
          prefix-icon="el-icon-lock"
          show-password
        ></el-input>
      </p>
      <p style="margin-top: 50px">
        <button class="btn" @click="loading">登陆</button>
      </p>
      <p style="margin-top: 20px; text-align: center">还没有账号？立即注册</p>
    </div>
  </div>
</template>

<script>
import { login } from '../api/loading'
import { setToken } from '../untils/auth'
export default {
  data() {
    return {
      form: {
        mobile: '13800000002',
        password: '123456'
      }
    }
  },
  methods: {
    loading() {
      if (this.form.mobile == '' || this.form.password == '') {
        return
      } else {
        login(this.form).then((res) => {
          console.log(res)
          if (res.data.code == 10000) {
            setToken(res.data.data)
            this.$router.replace('/userIndex')
            this.$message({
              message: '登陆成功',
              type: 'success'
            })
          } else {
            this.$message.error(res.data.message)
          }
        })
      }
    }
  },
  created() {},
  mounted() {},
  components: {},
  computed: {},
  watch: {}
}
</script>

